def find_first_unique_sequence(chars:, length:)
  chars.each_cons(length).with_index do |sequence, index|
    return index + length if sequence.uniq.length == length
  end
end

input = DATA.read.chars
p "a: #{find_first_unique_sequence(chars: input, length: 4)}"
p "b: #{find_first_unique_sequence(chars: input, length: 14)}"

__END__

